import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const Title = styled.h1`
  color: ${({ theme }) => theme.textColor};
  margin-bottom: 20px;
`;

const Home = () => {
  return (
    <Container>
      <Title>Welcome to ContentKosh</Title>
      <p>Your one-stop solution for content management</p>
    </Container>
  );
};

export default Home;
